sys_script_client.script:
function onChange( control, oldValue, newValue, isLoading, isTemplate ) 
{
	// Modified the if to return if the newValue == oldValue to avoid
	// unecessary trips to the server
	if( isLoading || newValue === '' || newValue == oldValue ) 
	{
		return;
	}

	// Instantiate the GetEmailAddress Script Include 
	var getOtherFieldValue = new GlideAjax( 'GetOtherFieldValue' );
	// Specify the getEmail method
	getOtherFieldValue.addParam( 'sysparm_name', 'getOtherValue' );
	// Pass the Requested for sys_id
	getOtherFieldValue.addParam( 'sysparm_userID', g_form.getValue( 'u_requested_for' ) );
	// Send the request to the server
	getOtherFieldValue.getXML( populateOtherField );


	// When the response is back from the server
	function populateOtherField( response )
	{
		// Extract the other value address from the response, clear any value from the email field, 
		// set new value in the email field
		var othervalueFromScriptInclude = response.responseXML.documentElement.getAttribute( "answer" );
		g_form.clearValue( 'u_requested_for_other_value' );
		g_form.setValue( 'u_requested_for_other_value', othervalueFromScriptInclude );
	}
}
