sys_script_include.script:
var GetOtherFieldValue = Class.create();
GetOtherFieldValue.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getOtherValue: function() {
		var userRecord = new GlideRecord( "sys_user" );
		userRecord.get( this.getParameter( 'sysparm_userID' ) );
		return userRecord.sys_id + '';
	},
    type: 'GetOtherFieldValue'
});
